function closeAllTabs(){for(var a=0;a<tabs.length;a++)tabs[a].classList.contains("active")&&tabs[a].classList.remove("active")}function closeSideMenus(){rightMenu.classList.contains("extended")?rightMenu.classList.remove("extended"):leftMenu.classList.contains("extended")&&leftMenu.classList.remove("extended"),menuCloseTarget.style.display="none"}function openTab(a){closeAllTabs(),tabs[a].classList.add("active"),closeSideMenus()}var tabs=document.querySelectorAll(".tab"),leftMenu=document.getElementById("left_mobile_side_menu"),rightMenu=document.getElementById("right_mobile_side_menu"),menuCloseTarget=document.getElementById("menu_close_target");